import Foundation


func getBotResponse(message: String) -> String {
    let tempMessage = message.lowercased()
    
    if tempMessage.contains("hello") {
        return "Hey there!"
    } else if tempMessage.contains("goodbye") {
        return "Talk to you later!"
    } else if tempMessage.contains("how are you") {
        return "I'm fine, how about you?"
    } else if (tempMessage.contains("trivia")) {
        return "Here is some trivia! " + trivia.randomElement()!
    } else if (tempMessage.contains("joke")) {
        return "Here a joke for you! " + jokes.randomElement()!
    } else if (tempMessage.contains("day")) {
        return day.randomElement()! + " What about you?"
    } else if (tempMessage.contains("rock")) {
        computer = rockPaperScissors.randomElement()!
            if (computer=="rock"){
                return "I had rock too! We tied!"
            }
            else if (computer=="paper"){
                return "I had paper! I win!"
            }
            else {
                return "I had scissors! You win!"
            }
        } else if (tempMessage.contains("paper")) {
            computer = rockPaperScissors.randomElement()!
            if (computer=="paper"){
                return "I had paper too! We tied!"
            }
            else if (computer=="scissors"){
                return "I had scissors! I win!"
            }
            else {
                return "I had rock! You win!"
            }
        } else if (tempMessage.contains("scissors")) {
            computer = rockPaperScissors.randomElement()!
            if (computer=="scissors"){
                return "I had scissors too! We tied!"
            }
            else if (computer=="rock"){
                return "I had rock! I win!"
            }
            else {
                return "I had paper! You win!"
            }
        } else if tempMessage.contains("good") || tempMessage.contains("great") || tempMessage.contains("awesome"){
            return good.randomElement()!
        } else if tempMessage.contains("bad") || tempMessage.contains("terrible") || tempMessage.contains("awful"){
            return bad.randomElement()!
        } else if(tempMessage.contains("favorite")) {
            return "I can not answer subjective inputs."
        } else if(tempMessage.contains("computer")) {
            return computertrivia.randomElement()!
        } else if tempMessage.contains("who") || tempMessage.contains("Who") || tempMessage.contains("you"){
            return "I am a program created by Mr. Groeneveld. I can respond to certain input."
        } else if tempMessage.contains("help"){
            return helpAnswers.randomElement()!
        } else if tempMessage.contains("random"){
               return "Here is a random number: " + String(Int.random(in: 1..<100))
        } else if tempMessage.contains(" no") || tempMessage.contains(" no ") || tempMessage.contains("no ") || tempMessage.contains("no"){
            return negative.randomElement()! + " " + helpAnswers.randomElement()!
        } else if tempMessage.contains(" yes") || tempMessage.contains(" yes ") || tempMessage.contains("yes ") || tempMessage.contains("yes"){
            return positives.randomElement()! + " "  + helpAnswers.randomElement()!
        } else {
        return randomAnswers.randomElement()!
        }
}
var computer = ""
var rockPaperScissors = ["rock", "paper", "scissors"]
